//SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

contract Voting {
    uint256 public nextElectionId = 1;

    // Election[] public elections;
    mapping(uint256 => Election) elections;
    uint256[] public allElectionList;

    mapping(address => Voter) voters;

    struct Election {
        uint256 electionId;
        string electionName;
        address creatorId;
        string password;
        bool hasPassword;
        uint256 startDate;
        uint256 endDate;
        bool isActive;
        string[] candidates;
        // Voter[] voter;
    }

    struct Voter {
        address voterId;
        bool voted;
        string firstName;
        string lastName;
        // Election[] participatedElection;
    }

    // mapping(address => Voter) voters;
    // address[] public votersList;

    function registerUser(
        address voterId,
        string memory firstName,
        string memory lastName
    ) public {
        Voter storage user = voters[voterId];
        user.voterId = voterId;
        user.firstName = firstName;
        user.lastName = lastName;
    }

    function getUser(address id)
        public
        view
        returns (
            address,
            string memory,
            string memory
        )
    {
        if (address(voters[id].voterId) == address(0)) {
            revert("User not found!");
        } else {
            
        return (voters[id].voterId, voters[id].firstName, voters[id].lastName);
        }
    }

    function createElection(
        string memory electionName,
        address creatorId,
        string memory password,
        bool hasPassword,
        uint256 startDate,
        uint256 endDate,
        bool isActive,
        string[] memory candidates
    ) public {
        Election storage newElection = elections[nextElectionId];
        newElection.electionId = nextElectionId;
        newElection.electionName = electionName;
        newElection.creatorId = creatorId;
        newElection.password = password;
        newElection.hasPassword = hasPassword;
        newElection.startDate = startDate;
        newElection.endDate = endDate;
        newElection.isActive = isActive;
        newElection.candidates = candidates;
        nextElectionId++;
        allElectionList.push(nextElectionId);
    }

    function getAnElection(uint256 id)
        public
        view
        returns (uint256, string memory)
    {
        return (elections[id].electionId, elections[id].electionName);
    }

    // function findElection(uint256 id) internal view returns (uint256) {
    //     for (uint256 i = 0; i < elections.length; i++) {
    //         if (elections[i].electionId == id) {
    //             return i;
    //         }
    //     }
    //     revert("Election does not exist!");
    // }

    // function getAllElections() public view returns (Election[] memory) {
    //     return elections;
    // }
}
